/**
 * define variables here
 **/
const int maxn = 1000 * 100 + 5;
const int mod = 998244353;
int n;

/**
 * define functions here
 **/

int op(int a, int b) { return a; }

int e() { return 1; }

struct F {
    int t;
    int k;
};

int add(int a, int b) { return (a + b) % mod; }
int mul(int a, int b) { return (1ll * a * b) % mod; }

int mapping(F f, int x) { return add(mul(f.t, x), f.k); }

F composition(F f, F g) { return F{mul(f.t, g.t), add(mul(f.t, g.k), f.k)}; }

int fast_pow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) {
            res = mul(res, a);
        }
        a = mul(a, a);
        b >>= 1;
    }
    return res;
}

int reverse(int a) { return fast_pow(a, mod - 2); }

F id() { return F{1, 0}; }

auto solve() {
    /**
     * main logic goes here
     **/
    int m;
    std::cin >> n >> m;
    vi a = Reader::vector<int>(n);
    atcoder::lazy_segtree<int, op, e, F, mapping, composition, id> sg(a);
    while (m--) {
        int l, r, x;
        std::cin >> l >> r >> x;
        int length = r - l + 1;
        int rev = reverse(length);
        sg.apply(l - 1, r, F{mul(add(length, mod - 1), rev), mul(x, rev)});
    }
    vi ans(n);
    for (int i: range(n)) {
        ans[i] = sg.get(i);
    }
    return ans;
}

int second_main() {
    auto ans = solve();
    for (int i: range(sz(ans))) {
        std::cout << ans[i] << " \n"[i == sz(ans) - 1];
    }
    // std::cout << ans << std::endl;
    return 0;
}

