/**
 * printing tuples
 **/
template <size_t n, typename... T>
typename enable_if<(n >= sizeof...(T))>::type
__tuple_printer(ostream &os, const tuple<T...> &tup){};

template <size_t n, typename... T>
typename enable_if<(n < sizeof...(T))>::type
__tuple_printer(ostream &os, const tuple<T...> &tup) {
    if (n != 0)
        os << " ";
    os << get<n>(tup);
    __tuple_printer<n + 1>(os, tup);
};

template <typename... T>
std::ostream &operator<<(std::ostream &os, const std::tuple<T...> &tup) {
    os << "(";
    __tuple_printer<0>(os, tup);
    os << ")";
    return os;
}

/**
 * printing pairs
 **/
template <typename T, typename K>
ostream &operator<<(std::ostream &os, const std::pair<T, K> &p) {
    os << "(" << p.first << " " << p.second << ")";
    return os;
}

/**
 * printing vectors
 **/
template <class T>
std::ostream &operator<<(std::ostream &os, const std::vector<T> &v) {
    os << "[";
    bool first = true;
    for (auto ii = v.begin(); ii != v.end(); ++ii) {
        if (first)
            first = false;
        else
            os << " ";
        os << (*ii);
    }
    os << "]";
    return os;
}

template <typename T, typename K = function<void(void)>>
void smin(
    T &a, T b, const K callable = []() {}) {
    if (a > b) {
        a = b;
        callable();
    }
}

template <typename T, typename K = function<void(void)>>
void smax(
    T &a, T b, const K callable = []() {}) {
    if (a < b) {
        a = b;
        callable();
    }
}

/**
 * range functions
 **/
inline vi range(int idx, int n) {
    if (n <= idx)
        return vi();
    vi indices(n - idx);
    for (int i = idx; i < n; ++i)
        indices[i - idx] = i;
    return indices;
}

inline vi range(int n) { return range(0, n); }

inline vi rrange(int idx, int n) {
    vi order = range(idx, n);
    reverse(all(order));
    return order;
}

inline vi rrange(int n) { return rrange(0, n); }

// define variables here
#define MAX_M 15
int n;
vi arr;

// define functions here

int read_input() {
    cin >> n;
    arr.resize(n);
    for (int i: range(n)) {
        cin >> arr[i];
        arr[i]--;
    }
    return 0;
}

auto solve() {
    /**
     * main logic goes here
     **/
    vector<vi> dp(n + 1, vi(n + 1, 1e9));
    for (int i: range(n + 1)) {
        dp[n][i] = 0;
    }
    vector<vector<pii>> pep(n + 1, vector<pii>(n + 1, {-1, -1}));

    for (int i: rrange(n)) {
        for (int j: range(0, n + 1)) {
            int &cur = dp[i][j];
            pii &dad = pep[i][j];
            // second scenario
            int idx = i;
            if (j - 1 >= 0) {
                smin(cur, n - idx - 1);
                for (int k: range(idx + 1, n))
                    if (arr[k] > arr[i]) {
                        smin(cur,
                             k - idx - 1 + dp[k][j - (k - idx - 1 > 0 ? 1 : 0)],
                             [&]() {
                                 dad = {k, j - (k - idx - 1 > 0 ? 1 : 0)};
                             });
                    }
            } else {
                while (idx + 1 < n && arr[idx] < arr[idx + 1])
                    idx++;
                if (idx == n - 1)
                    cur = 0;
            }
        }
    }
    for (int i: range(1, n + 1)) {
        int ans = 1e9;
        int idx = -1;
        int cnt = 0;
        for (int j: range(n)) {
            if (j > 0 && arr[j - 1] != j - 1)
                cnt = 1;
            smin(ans, j + dp[j][i - cnt], [&]() { idx = j; });
        }
        // trace(ans _ i _ idx);
        cout << ans << ' ';
    }
    return "";
}

int second_main() {
    read_input();
    auto ans = solve();
    cout << ans << endl;
    return 0;
}

