template <typename T, typename K>
ostream &operator<<(std::ostream &os, const std::pair<T, K> &p) {
    os << "(" << p.first << " " << p.second << ")";
    return os;
}

/**
 * printing vectors
 **/
template <class T>
std::ostream &operator<<(std::ostream &os, const std::vector<T> &v) {
    os << "[";
    bool first = true;
    for (auto ii = v.begin(); ii != v.end(); ++ii) {
        if (first)
            first = false;
        else
            os << " ";
        os << (*ii);
    }
    os << "]";
    return os;
}

/**
 * range functions
 **/
inline vi range(int idx, int n) {
    if (n <= idx)
        return vi();
    vi indices(n - idx);
    for (int i = idx; i < n; ++i)
        indices[i - idx] = i;
    return indices;
}

inline vi range(int n) { return range(0, n); }

// define variables here
#define MAX_M 15
int n;
int m;
int init_mask;
vector<pair<int, pii>> medicines;
vi dp;

// define functions here
int str_to_mask(string str) {
    int mask = 0;
    for (int i: range(sz(str))) {
        mask = mask * 2 + (str[i] == '1' ? 1 : 0);
    }
    return mask;
}

int read_input() {
    cin >> n >> m;
    string str;
    cin >> str;
    init_mask = str_to_mask(str);
    medicines.resize(m);
    for (int j: range(m)) {
        auto &t = medicines[j];
        cin >> t.X;
        cin >> str;
        t.Y.X = str_to_mask(str);
        cin >> str;
        t.Y.Y = str_to_mask(str);
    }
    return 0;
}

template <typename T>
void smin(
    T &a, T b, function<void(void)> callable = []() {}) {
    if (a > b) {
        a = b;
        callable();
    }
}

template <typename T>
void smax(
    T &a, T b, function<void(void)> callable = []() {}) {
    if (a < b) {
        a = b;
        callable();
    }
}

auto solve() {
    /**
     * main logic goes here
     **/
    dp.assign(1 << n, 1e9);
    priority_queue<pii> pq;
    pq.push({0, init_mask});
    dp[init_mask] = 0;
    while (!pq.empty()) {
        pii head = pq.top();
        pq.pop();
        int dist = -head.X;
        int mask = head.Y;
        if (dp[mask] < dist)
            continue;
        if (mask == 0)
            return dist;
        for (auto medicine: medicines) {
            int alter = mask | medicine.Y.Y;
            alter ^= alter & medicine.Y.X;
            smin(dp[alter], dp[mask] + medicine.X, [&]() {
                pq.push({-dp[alter], alter});
            });
        }
    }
    return -1;
}

int second_main() {
    read_input();
    auto ans = solve();
    cout << ans << endl;
    return 0;
}

