template <class T>
std::ostream &operator<<(std::ostream &os, const std::vector<T> &v) {
    os << "[";
    bool first = true;
    for (auto ii = v.begin(); ii != v.end(); ++ii) {
        if (first) {
            first = false;
        } else {
            os << " ";
        }
        os << (*ii);
    }
    os << "]";
    return os;
}

template <typename T, typename K = std::function<void(void)>>
void smin(
    T &a, T b, const K callable = []() {}) {
    if (a > b) {
        a = b;
        callable();
    }
}

template <typename T, typename K = std::function<void(void)>>
void smax(
    T &a, T b, const K callable = []() {}) {
    if (a < b) {
        a = b;
        callable();
    }
}

/**
 * range functions
 **/
inline vi range(int idx, int n) {
    if (n <= idx) {
        return vi();
    }
    vi indices(n - idx);
    for (int i = idx; i < n; ++i) {
        indices[i - idx] = i;
    }
    return indices;
}

inline vi range(int n) { return range(0, n); }

inline vi rrange(int idx, int n) {
    vi order = range(idx, n);
    reverse(all(order));
    return order;
}

inline vi rrange(int n) { return rrange(0, n); }

class Reader {
  public:
    template <typename T> inline static T primitive() {
        T temp;
        std::cin >> temp;
        return temp;
    }

    template <typename T> static std::vector<T> vector(int n) {
        std::vector<T> res(n);
        for (int &i: range(n)) {
            res[i] = Reader::primitive<T>();
        }
        return res;
    }

    static void sync() {
        std::ios_base::sync_with_stdio(false);
        std::cin.tie(nullptr);
    }
};

/**
 * define variables here
 **/
const int maxn = 1000 * 100 + 5;
int n;

/**
 * define functions here
 **/

void dfs(int u, vi &order, vi &colors, vvi &adj) {
    colors[u] = 1;
    for (int v: adj[u]) {
        if (colors[v] == 0) {
            dfs(v, order, colors, adj);
        }
    }
    order.push_back(u);
}

auto solve() {
    /**
     * main logic goes here
     **/
    int k;
    std::cin >> n >> k;
    vi cs = Reader::vector<int>(n);
    vi ps = Reader::vector<int>(k);
    for (int &t: ps) {
        t--;
    }
    vvi adj;
    adj.assign(n, vi());
    for (int i: range(n)) {
        int temp;
        std::cin >> temp;
        if (temp) {
            vi t;
            t = Reader::vector<int>(temp);
            for (int idx: t) {
                adj[i].push_back(idx - 1);
            }
        }
    }

    vi order;
    vi colors(n, 0);
    for (int i: range(n)) {
        if (colors[i] == 0) {
            dfs(i, order, colors, adj);
        }
    }
    vl dp(n, 1e18);
    for (int i: ps) {
        dp[i] = 0;
    }
    for (int i: order) {
        ll &res = dp[i];
        smin(res, (ll)cs[i]);
        if (sz(adj[i])) {
            ll sum = 0;
            for (int j: adj[i]) {
                sum += dp[j];
            }
            smin(res, sum);
        }
    }
    return dp;
}

int second_main() {
    auto ans = solve();
    char array = true;
    if (array) {
        for (int i: range(sz(ans))) {
            std::cout << ans[i] << " \n"[i == sz(ans) - 1];
        }
    } else {
        std::cout << ans << std::endl;
    }
    return 0;
}

