/**
 * printing tuples
 **/
template <size_t n, typename... T>
typename enable_if<(n >= sizeof...(T))>::type
__tuple_printer(ostream &os, const tuple<T...> &tup){};

template <size_t n, typename... T>
typename enable_if<(n < sizeof...(T))>::type
__tuple_printer(ostream &os, const tuple<T...> &tup) {
    if (n != 0)
        os << " ";
    os << get<n>(tup);
    __tuple_printer<n + 1>(os, tup);
};

template <typename... T>
std::ostream &operator<<(std::ostream &os, const std::tuple<T...> &tup) {
    os << "(";
    __tuple_printer<0>(os, tup);
    os << ")";
    return os;
}

/**
 * printing pairs
 **/
template <typename T, typename K>
ostream &operator<<(std::ostream &os, const std::pair<T, K> &p) {
    os << "(" << p.first << " " << p.second << ")";
    return os;
}

/**
 * printing vectors
 **/
template <class T>
std::ostream &operator<<(std::ostream &os, const std::vector<T> &v) {
    os << "[";
    bool first = true;
    for (auto ii = v.begin(); ii != v.end(); ++ii) {
        if (first)
            first = false;
        else
            os << " ";
        os << (*ii);
    }
    os << "]";
    return os;
}

/**
 * range functions
 **/
inline vi range(int idx, int n) {
    if (n <= idx)
        return vi();
    vi indices(n - idx);
    for (int i = idx; i < n; ++i)
        indices[i - idx] = i;
    return indices;
}

inline vi range(int n) { return range(0, n); }

// define variables here
#define MAX_M 15
int n;
string str;
vector<pii> queries;
const int mod = 998244353;

// define functions here

int read_input() {
    int m;
    cin >> n >> m;
    cin >> str;
    queries.resize(m);
    for (int i: range(m)) {
        int t;
        char ch;
        cin >> t >> ch;
        queries[i] = {t - 1, ch};
    }
    return 0;
}

int mul(int a, int b) { return (1LL * a * b) % mod; }
int fast_pow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) {
            res = mul(res, a);
        }
        a = mul(a, a);
        b >>= 1;
    }
    return res;
}
auto solve() {
    /**
     * main logic goes here
     **/
    int ans = 1;
    for (int i: range(1, n - 1)) {
        ans = str[i] == '?' ? mul(ans, i) : ans;
    }
    vi res;
    if (str[0] == '?')
        res.push_back(0);
    else
        res.push_back(ans);
    for (auto &[idx, ch]: queries) {
        if (str[idx] == '?') {
            if (idx != 0)
                ans = mul(ans, fast_pow(idx, mod - 2));
        }
        str[idx] = ch;
        if (str[idx] == '?') {
            if (idx != 0)
                ans = mul(ans, idx);
        }
        if (str[0] == '?')
            res.push_back(0);
        else
            res.push_back(ans);
    }
    return res;
}

int second_main() {
    read_input();
    auto ans = solve();
    for (int t: ans)
        cout << t << endl;
    return 0;
}

