func longestSubarray(nums []int) int {
	n := len(nums)
	var ri []int = make([]int, n)
	for i := range n {
		ri[i] = 0
	}

	for i := range n - 2 {
		if nums[i] + nums[i + 1] == nums[i + 2] {
			ri[i] = 1
		}
	}

	res := 0
	cur := 0
	for i := range n {
		if ri[i] == 1 {
			cur++
			continue
		}
		res = max(res, cur + 2)
		cur = 0
	}

	return res
}

