class Solution {
public:
  std::vector<int> minOperations(std::vector<int> &nums) {
    int n = sz(nums);
    vi res;
    vi candids;

    auto generate_candids = [&](int bits, vi &candids) {
      int length = bits >> 1;
      if (bits % 2 == 0) {
        for (int i = 1; i < (1 << length); i+=2) {
          int candid = 0;
          for (int j = 0; (1 << j) <= i; j++) {
            if ((i >> j) & 1) {
              candid |= (1 << j);
              candid |= (1 << (bits - 1 - j));
            }
          }
          candids.push_back(candid);
        }
      } else {
        for (int i = 1; i < (1 << length); i+=2) {
          int candid = 0;
          for (int j = 0; (1 << j) <= i; j++) {
            if ((i >> j) & 1) {
              candid |= (1 << j);
              candid |= (1 << (bits - 1 - j));
            }
          }
          candids.push_back(candid);
          candids.push_back(candid | (1 << length));
        }
      }
    };

    for (int i = 15; i >= 1; i--) {
      generate_candids(i, candids);
    }
    candids.push_back(1);
    for (int &t : candids) {
      std::cout << t << " ";
    }
    std::cout << std::endl;
    sort(all(candids));
    candids.erase(unique(all(candids)), candids.end());
    for (int t : nums) {
      int idx = std::lower_bound(all(candids), t) - candids.begin();
      int cur = candids[idx] - t;
      if (idx - 1 >= 0) {
        smin(cur, t - candids[idx - 1]);
      }
      res.push_back(cur);
    }
    return res;
  }
};

