/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left),
 * right(right) {}
 * };
 */
void smax(int &a, int b) {
    if (a < b)
        a = b;
}
class Solution {
    int best_ans;

  public:
    vi go(TreeNode *root) {
        int bt = 1;
        int ss = root->val;
        int maxi = root->val;
        int mini = root->val;
        if (root->right != nullptr) {
            vi temp = go(root->right);
            if (temp[0] == 1) {
                smax(maxi, temp[1]);
                smax(mini, temp[3]);
                ss += temp[4];
                if (root->val >= mini)
                    bt = 0;
            } else {
                bt = 0;
            }
        }
        if (root->left != nullptr) {
            vi temp = go(root->left);
            if (temp[0] == 1) {
                smax(maxi, temp[1]);
                smax(mini, temp[2]);
                ss += temp[3];
                if (root->val <= maxi)
                    bt = 0;
            } else {
                bt = 0;
            }
        }
        if (bt)
            smax(best_ans, ss);
        return {bt, maxi, mini, ss};
    }
    int maxSumBST(TreeNode *root) {
        best_ans = -1.7e9;
        go(root);
        return best_ans;
    }
};

