auto solve() {
  /**
   * main logic goes here
   **/
  int n, k;
  std::cin >> n >> k;
  vi a = Reader::vector<int>(n);

  auto solve = [&](const std::vector<double> &b) -> std::pair<double, int> {
    std::vector<std::pair<double, int>> dp(n + 1, {-1e15, 0});
    dp[n] = {0, 0};
    auto eq = [](double x, double y) { return std::fabs(x - y) < 1e-9; };
    rrange(i, n) {
      smax(dp[i], dp[i + 1]);
      if (i + 2 < n) {
        if (eq(dp[i].first, b[i] + dp[i + 2].first) &&
            dp[i + 2].second + 1 > dp[i].second) {
          dp[i].second = dp[i + 2].second + 1;
        } else {
          if (dp[i].first < b[i] + dp[i + 2].first) {
            dp[i] = {b[i] + dp[i + 2].first, dp[i + 2].second + 1};
          }
        }
      } else {
        smax(dp[i], {b[i], 1});
      }
    }
    return dp[0];
  };

  double s = 0;
  double e = -1e9;
  double last_value = -1e9;
  while (s - e > 1e-6) {
    double mid = (e + s) / 2;
    std::vector<double> b(n);
    for (int i = 0; i < n; ++i) {
      b[i] = a[i] + mid;
    }
    auto [best, cnt] = solve(b);
    last_value = mid;
    if (cnt < k) {
      e = mid;
    } else {
      s = mid;
    }
  }

  auto [best, cnt] = solve([&]() {
    std::vector<double> b(n);
    for (int i = 0; i < n; ++i) {
      b[i] = a[i] + s;
    }
    return b;
  }());
  best -= k * s;
  return ll(best + 1e-9);


  return -1ll;
}

